import React from 'react';
import cookie from 'react-cookies';
import { Button,Modal } from 'react-materialize';

//importing developer made components//
import SingleItem from './SingleItem';
import Form from './Form';
//importing ends//

class Items extends React.Component{
    constructor(){
        super();
        this.state={cookies:[],formRender:false};

        this.createTask=this.createTask.bind(this);
        this.deleteTask=this.deleteTask.bind(this);
        this.updateTask=this.updateTask.bind(this);
    }

    updateTask(info,id){
        console.log(info);
        const data=this.state.cookies.map(i=>{
            if(i.serialNumber===id){
                i.Title=info.Title;
                i.Description=info.Description;
                i.Priority=info.Priority;
                i.Date=new Date();
            }
            return i;
        });
        cookie.save('tasks',data,{path:'/'});
        this.setState({cookies:data});

    }

    deleteTask(id,Title){
        let data=[];
        if (window.confirm("Are you sure you want to delete this message "+Title)){
            data=this.state.cookies.filter(i=>{
                if(i.serialNumber !== id)
                    return i;
            })
            if(data.length===0)
                cookie.save('tasks',undefined,{path:'/'});
            else
                cookie.save('tasks',data,{path:'/'});
            this.setState({cookies:cookie.load('tasks')});
        }
            else
                console.log("no");
    }

    createTask(info){
        console.log(info);
        let data=this.state.cookies;
        console.log(data);
        // const info={
        //     Title:"title",
        //     Description:"description",
        //     Date:new Date(),
        //     Priority:"High",
        //     serialNumber:0
        // }
        if(data===undefined){
            data=[];
            info.serialNumber=1;
        }
        else
            info.serialNumber=data.length+1;
        data.push(info);
        cookie.save('tasks',data,{path:'/'});
        this.setState({cookies:data})
        // console.log(cookie.load('tasks'))
    }

    componentDidMount(){
        const data=cookie.load('tasks');
        this.setState({cookies:data});
        // console.log(data);
    }

    render(){
        conso
        if(this.state.cookies.length===0||this.state.cookies===undefined){
            return(
                <div style={{textAlign:'center'}}>
                    <h1 style={{textAlign:"center"}}>No data to show</h1>
                    <Modal header="Modal Header" trigger={<Button />}>
                           <Form create={this.createTask}/>
                    </Modal>
                </div>
            );
        }
        else{
            const data=this.state.cookies.map(i=>{
                return(
                    <div>
                        <SingleItem style={{display:'inline-block'}} data={i} 
                            delete={this.deleteTask}
                            update={this.updateTask}
                        />
                    </div>
                )
            })
            return(
                <div style={{textAlign:'center'}}>
                    {data}
                    <Modal header="Modal Header" trigger={<Button />}>
                           <Form create={this.createTask}/>
                    </Modal>
                </div>
            );
        }
    }
}


export default Items;
