import React from 'react';
// import {Modal,Table,Button,Checkbox} from 'react-materialize';
import {Modal,Button,Table} from 'react-bootstrap'

//developer made module//
import Form from './Form'
//developer made module

class SingleItem extends React.Component{
    constructor(props){
        super(props);
        // console.log(this.props.data);
    }


    render(){
        return(
            <div>
               <Table  striped bordered hover>
                   <thead></thead>
                    <tbody>
                    <tr>
                    <td>
                    {this.props.data.serialNumber}
                    </td>
                    <td>
                    {this.props.data.Title}
                    </td>
                    <td>
                    {this.props.data.Description}
                    </td>
                    <td>
                    {this.props.data.Priority}
                    </td>
                    <td>
                    <Modal header="Options" trigger={<Button/>}>
                        <p>
                            {this.props.data.Description}
                        </p>
                        {/* <Checkbox onChange={()=>{this.props.delete(this.props.data.serialNumber,this.props.data.Title)}} label="Delete"/> */}
                        <br/><br/>
                        <Modal header="Update Your list" trigger={<Button />}>
                           <Form data={this.props.data} update={this.props.update}/>
                        </Modal>
                    </Modal>
                    </td>
                    </tr>
                    </tbody>
                    </Table>

            </div>
        )
    }
}
export default SingleItem;
