import React from 'react';
import cookie from 'react-cookies';
import { Button } from 'react-materialize';

//importing developer made components//
import SingleItem from './SingleItem';
//importing ends//

class Items extends React.Component{
    constructor(){
        super();
        this.state={cookies:[]};
        this.createTask=this.createTask.bind(this);
        this.deleteTask=this.deleteTask.bind(this);
    }

    deleteTask(id){
        if (confirm("Are you sure you want to delete this message")){
            console.log(`Delete task ${id}`);
        }
            else
                console.log("no");
    }

    createTask(){
        let data=this.state.cookies;
        console.log(data);
        const info={
            Title:"title",
            Description:"description",
            Date:new Date(),
            Priority:"High",
            serialNumber:0
        }
        if(data===undefined){
            data=[];
            info.serialNumber=1;
        }
        else
            info.serialNumber=data.length+1;
        data.push(info);
        cookie.save('tasks',data,{path:'/'});
        this.setState({cookies:data})
        console.log(cookie.load('tasks'))
    }

    componentDidMount(){
        const data=cookie.load('tasks');
        this.setState({cookies:data});
        // console.log(data);
    }

    render(){
        if(this.state.cookies==undefined){
            return(
                <div>
                    <h1 style={{textAlign:"center"}}>No data to show</h1>
                    <Button onClick={this.createTask}>Create Task</Button>
                </div>
            )
        }
        else{
            const data=this.state.cookies.map(i=>{
                return(
                    <div>
                        <SingleItem style={{display:'inline-block'}} data={i} 
                            deleteTask={this.deleteTask}
                        />
                    </div>
                )
            })
            return(
                <div>
                    {data}
                    <Button onClick={this.createTask}>Create Task</Button>
                </div>
            )
        }
    }
}


export default Items;
