import React from 'react';
import cookie from 'react-cookies';
import { Button } from 'react-materialize';

//importing developer made components//
import SingleItem from './SingleItem';
//importing ends//

class Items extends React.Component{
    constructor(){
        super();
        this.state={cookies:[]};
        this.createTask=this.createTask.bind(this);
    }

    createTask(){
        let data=this.state.cookies;
        console.log(data);
        const info={
            Title:"title",
            Description:"description",
            Date:new Date(),
            Priority:"High",
            serialNumber:data.length+1
        }
        if(data===undefined)
            data=[];
        data.push(info);
        cookie.save('tasks',data,{path:'/'});
        this.setState({cookies:data})
        console.log(cookie.load('tasks'))
    }

    componentDidMount(){
        const data=cookie.load('tasks');
        this.setState({cookies:data});
        // console.log(data);
    }

    render(){
        if(this.state.cookies==undefined){
            return(
                <div>
                    <h1 style={{textAlign:"center"}}>No data to show</h1>
                    <Button onClick={this.createTask}>Create Task</Button>
                </div>
            )
        }
        else{
            const data=this.state.cookies.map(i=>{
                return(
                    <SingleItem data={i}/>
                )
            })
            return(
                <div>
                    {data}
                    <Button onClick={this.createTask}>Create Task</Button>
                </div>
            )
        }
    }
}


export default Items;
